import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

const AddFriend = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email,setEmail]=useState('')


  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Gerçek bir API endpoint kullanılmalıdır
    const apiUrl = 'http://localhost:9000/api/addfriends';

    // Yeni karakter verisi
    const newCharacter = {
      name,
      age,
      email,
    };

    try {
      // Token kontrolü
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('Token not found. User is not logged in.');
        return;
      }

      // API çağrısı
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newCharacter),
      });

      if (response.ok) {
        console.log('Character added successfully!');
        // Başarıyla eklendiyse, isteği yeniden yönlendirebilirsiniz (örneğin, /friends sayfasına)
      } else {
        console.error('Error adding character:', response.statusText);
      }
    } catch (error) {
      console.error('Error adding character:', error);
    }
  };

  return (
    <div class="container">
      <h2>Add Friend</h2>
      <form onSubmit={handleFormSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required  class="form-control"/>
        </label>
        <br />
        <label>
          Age:
          <input type="text" value={age} onChange={(e) => setAge(e.target.value)} required  class="form-control"/>
        </label>

        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setAge(e.target.value)} required  class="form-control"/>
        </label>
        <br />

        <button type="submit">Add Friend</button>
      </form>
    </div>
  );
};

export default AddFriend;
